import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Navigation.css';

const Navigation = ({ onAuthClick }) => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
    setShowUserMenu(false);
    setShowMobileMenu(false);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="navigation">
      <div className="nav-container">
        <Link to="/" className="nav-logo">
          <span>üíñ</span>
          UFAZ Slay Meter
        </Link>

        <button 
          className="mobile-menu-toggle"
          onClick={() => setShowMobileMenu(!showMobileMenu)}
        >
          <span className={`hamburger ${showMobileMenu ? 'active' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </span>
        </button>

        <div className={`nav-links ${showMobileMenu ? 'mobile-open' : ''}`}>
          <Link 
            to="/explore" 
            className={`nav-link ${isActive('/explore') ? 'active' : ''}`}
            onClick={() => setShowMobileMenu(false)}
          >
            Explore
          </Link>

          <Link 
            to="/vibe-wars" 
            className={`nav-link ${isActive('/vibe-wars') ? 'active' : ''}`}
            onClick={() => setShowMobileMenu(false)}
          >
            Vibe Wars
          </Link>

          <Link 
            to="/leaderboard" 
            className={`nav-link ${isActive('/leaderboard') ? 'active' : ''}`}
            onClick={() => setShowMobileMenu(false)}
          >
            Leaderboard
          </Link>

          {user ? (
            <>
              <Link 
                to="/dashboard" 
                className={`nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                onClick={() => setShowMobileMenu(false)}
              >
                Dashboard
              </Link>

              <Link 
                to="/send-vibe" 
                className={`nav-link ${isActive('/send-vibe') ? 'active' : ''}`}
                onClick={() => setShowMobileMenu(false)}
              >
                Send Vibe
              </Link>

              <Link 
                to={`/profile/${user.handle}`} 
                className={`nav-link ${location.pathname.includes('/profile') ? 'active' : ''}`}
                onClick={() => setShowMobileMenu(false)}
              >
                Profile
              </Link>

              <div className="user-menu">
                <button 
                  className="user-menu-trigger"
                  onClick={() => setShowUserMenu(!showUserMenu)}
                >
                  <div className="user-avatar">
                    {user.avatarUrl ? (
                      <img src={user.avatarUrl} alt={user.name} />
                    ) : (
                      <span>{user.name?.charAt(0).toUpperCase()}</span>
                    )}
                  </div>
                  <span className="user-name">{user.name}</span>
                </button>

                {showUserMenu && (
                  <div className="user-dropdown">
                    <Link 
                      to={`/profile/${user.handle}`}
                      className="dropdown-item"
                      onClick={() => { setShowUserMenu(false); setShowMobileMenu(false); }}
                    >
                      <span>üë§</span> My Profile
                    </Link>
                    <Link 
                      to="/settings"
                      className="dropdown-item"
                      onClick={() => { setShowUserMenu(false); setShowMobileMenu(false); }}
                    >
                      <span>‚öôÔ∏è</span> Settings
                    </Link>
                    <button 
                      onClick={handleLogout}
                      className="dropdown-item logout"
                    >
                      <span>üö™</span> Logout
                    </button>
                  </div>
                )}
              </div>
            </>
          ) : (
            <>
              <button 
                onClick={() => { onAuthClick(); setShowMobileMenu(false); }}
                className="btn btn-primary nav-auth-btn"
              >
                Sign In
              </button>
            </>
          )}
        </div>
      </div>

      {/* Wednesday Special Banner */}
      {new Date().getDay() === 3 && (
        <div className="wednesday-banner">
          <p>üéÄ It's Wednesday! Double slay points today! üéÄ</p>
        </div>
      )}
    </nav>
  );
};

export default Navigation;
